import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import { useNavigate } from 'react-router-dom';

const SOCKET_SERVER_URL = 'http://localhost:5001';

const ChatRoom = ({ room }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);
  const socketRef = useRef();
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const fullName = storedUser?.fullName || 'An√≥nimo';

    socketRef.current = io(SOCKET_SERVER_URL, {
      auth: { token },
    });

    socketRef.current.emit('joinRoom', { room, fullName });

    socketRef.current.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    socketRef.current.on('roomUsers', (usersList) => {
      const signosEmoji = {
        aries: '‚ôà', tauro: '‚ôâ', geminis: '‚ôä', cancer: '‚ôã', leo: '‚ôå',
        virgo: '‚ôç', libra: '‚ôé', escorpio: '‚ôè', sagitario: '‚ôê',
        capricornio: '‚ôë', acuario: '‚ôí', piscis: '‚ôì'
      };

      const usersWithEmoji = usersList.map((u) => {
        const cleanSign = (u.zodiacSign || '')
          .toLowerCase()
          .normalize('NFD')
          .replace(/[\u0300-\u036f]/g, '');
        return {
          ...u,
          emoji: signosEmoji[cleanSign] || ''
        };
      });
      console.log('Usuarios recibidos:', usersWithEmoji);
      setUsers(usersWithEmoji);
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, [room]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    socketRef.current.emit('chatMessage', {
      room,
      text: message,
    });

    setMessage('');
  };

  const handleBack = () => {
    navigate(-1);
  };

  return (
    <div style={styles.container}>
      {/* ‚úÖ Bot√≥n volver */}
      <button onClick={handleBack} style={styles.backButton}>
        ‚Üê Volver
      </button>

      <h2 style={styles.title}>
        Bienvenido a la sala: {room.charAt(0).toUpperCase() + room.slice(1)}
      </h2>

      <div style={styles.usersBox}>
        <h4 style={styles.usersTitle}>Usuarios conectados:</h4>
        {users.length === 0 ? (
          <p style={styles.usersText}>No hay usuarios</p>
        ) : (
          <ul style={styles.usersList}>
            {users.map((u) => (
              <li key={u.id} style={styles.userItem}>
                <span style={{ marginRight: 8 }}>{u.emoji}</span>{u.fullName}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div style={styles.chatBox}>
        {messages.map((msg, i) => (
          <div key={i}>
            <b>{msg.user || 'Anon'}:</b> {msg.text || '(sin texto)'}{' '}
            <small>({new Date(msg.timestamp).toLocaleTimeString()})</small>
          </div>
        ))}
      </div>

      <form onSubmit={sendMessage} style={styles.form}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Escrib√≠ tu mensaje..."
          style={styles.input}
          autoComplete="off"
        />
        <button type="submit" style={styles.sendButton}>
          Enviar
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: 20,
    backgroundImage: 'url("/logo/Zodiacus.jpeg")',
    backgroundSize: 'cover',
    backgroundColor: 'transparent',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundImageRendering: 'auto',
    imageRendering: 'auto',
    filter: 'brightness(1.05) contrast(1.1)',
    color: '#fff',
    minHeight: '100vh',
    position: 'relative', // üîë importante para posicionar el bot√≥n
  },
  title: {
    fontSize: '2rem',
    color: '#FFFFFF',
    fontWeight: '700',
    fontStyle: 'italic',
    fontFamily: "'Poppins', sans-serif",
    textAlign: 'center',
    textShadow: '2px 2px 6px rgba(0, 0, 0, 0.85)',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    padding: '10px 20px',
    borderRadius: 12,
    marginBottom: 20,
    maxWidth: 500,
    marginLeft: 'auto',
    marginRight: 'auto',
  },
  chatBox: {
    border: '1px solid #ccc',
    padding: 10,
    height: 400,
    overflowY: 'scroll',
    marginBottom: 10,
    backgroundColor: '#fff',
    color: '#000',
    borderRadius: 10,
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
  },
  form: {
    display: 'flex',
    alignItems: 'center',
    gap: 8,
    marginTop: 20,
  },
  input: {
    flex: 1,
    padding: 8,
    fontSize: '1rem',
  },
  sendButton: {
    padding: '8px 16px',
    backgroundColor: '#DAA520',
    color: '#4B0082',
    border: 'none',
    borderRadius: 8,
    fontWeight: 'bold',
    cursor: 'pointer',
  },
  backButton: {
  position: 'absolute',
  top: 20, // ‚Üê en vez de bottom
  left: 20,
    backgroundColor: '#DAA520',
    color: '#4B0082',
    border: 'none',
    borderRadius: 8,
    padding: '10px 15px',
    fontWeight: 'bold',
    cursor: 'pointer',
    zIndex: 10,
  },
  usersBox: {
    alignSelf: 'flex-start',
    backgroundColor: 'rgba(0, 0, 0, 0.4)',
    padding: '10px 15px',
    borderRadius: 10,
    color: '#fff',
    maxWidth: 300,
    fontFamily: "'Poppins', sans-serif",
    marginBottom: 20,
  },
  usersTitle: {
    fontSize: '1rem',
    fontWeight: 'bold',
    color: '#DAA520',
    marginBottom: 8,
  },
  usersList: {
    listStyle: 'none',
    paddingLeft: 0,
    margin: 0,
  },
  userItem: {
    fontSize: '0.95rem',
    marginBottom: 4,
    color: '#fff',
    fontWeight: '500',
  },
  usersText: {
    fontSize: '0.9rem',
    color: '#ccc',
  },
};

export default ChatRoom;
