import React from 'react';
import { useParams } from 'react-router-dom';

function RoomScreen() {
  const { signo } = useParams();

  return (
    <div style={{ padding: 20 }}>
      <h2>Sala de {signo.charAt(0).toUpperCase() + signo.slice(1)}</h2>
      <p>Aquí estará la sala de chat o actividad para {signo}</p>
      {/* Más adelante agregaremos el chat, interacción, etc */}
    </div>
  );
}

export default RoomScreen;
