import React, { useEffect, useState, useRef, useMemo } from 'react';
import { io } from 'socket.io-client';
import { useNavigate } from 'react-router-dom';
import EmojiPicker from 'emoji-picker-react';
import ChatRoom from '../components/ChatRoom';

const SOCKET_SERVER_URL = 'http://localhost:5001';
const BASE_URL = 'http://localhost:5001/uploads/';

const signosEmoji = {
  aries: '‚ôà', tauro: '‚ôâ', geminis: '‚ôä', cancer: '‚ôã', leo: '‚ôå',
  virgo: '‚ôç', libra: '‚ôé', escorpio: '‚ôè', sagitario: '‚ôê',
  capricornio: '‚ôë', acuario: '‚ôí', piscis: '‚ôì'
};

const ZodiacRouletteRoom = () => {
  const [partner, setPartner] = useState(null);
  const [waiting, setWaiting] = useState(true);
  const [room, setRoom] = useState(null);
  const socketRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const token = localStorage.getItem('token');
    if (!storedUser || !token) return;

    socketRef.current = io(SOCKET_SERVER_URL, { auth: { token } });

    socketRef.current.emit('joinRoulette', storedUser);

    socketRef.current.on('roulettePartner', (data) => {
      setPartner(data); // El objeto con fullName, zodiacSign, photoPath, room
      setRoom(data.room);
      setWaiting(false);

      // Unirse a la sala para recibir mensajes
      socketRef.current.emit('joinRoom', {
        room: data.room,
        fullName: storedUser.fullName,
        zodiacSign: storedUser.zodiacSign,
        photoPath: storedUser.photoPath,
      });
    });

    socketRef.current.on('waitingPartner', () => {
      setWaiting(true);
      setPartner(null);
    });

    // Cerrar conexi√≥n al desmontar
    return () => {
      socketRef.current.disconnect();
      setRoom(null);
    };
  }, []);

  const handleBack = () => {
    navigate(-1);
  };

  const cleanPhoto = (path) => {
    if (!path) return '/default-profile.png';
    if (path.startsWith('http')) return path;
    return `${BASE_URL}${path.replace(/^\/?uploads\//, '')}`;
  };

  const handleImgError = (e) => {
    e.currentTarget.onerror = null;
    e.currentTarget.src = '/default-profile.png';
  };

  // Preparar datos de partner para mostrar emoji y foto limpia
  const partnerWithExtras = useMemo(() => {
    if (!partner) return null;
    const cleanSign = (partner.zodiacSign || '')
      .toLowerCase()
      .normalize('NFD')
      .replace(/[\u0300-\u036f]/g, '');

    return {
      ...partner,
      emoji: signosEmoji[cleanSign] || '',
      photoPath: cleanPhoto(partner.photoPath),
    };
  }, [partner]);

  return (
    <div style={styles.container}>
      <button onClick={handleBack} style={styles.backButton}>
        ‚Üê Volver
      </button>

      <h2 style={styles.title}>üé≤ Ruleta Zodiacal üîÆ</h2>

      {waiting ? (
        <div style={styles.infoBox}>
          <h3 style={styles.infoTitle}>Buscando usuario...</h3>
          <p style={styles.infoText}>
            En esta sala de ruleta zodiacal, ser√°s emparejado aleatoriamente con otro usuario.
            El objetivo es crear nuevas conexiones, ya sea una charla casual, una amistad o lo que surja. ¬°Disfrut√° la experiencia c√≥smica!
          </p>
        </div>
      ) : (
        <>
          {partnerWithExtras && (
            <div style={styles.usersBox}>
              <h4 style={styles.usersTitle}>Tu pareja en la ruleta:</h4>
              <div style={styles.userItem}>
                <img
                  src={partnerWithExtras.photoPath}
                  alt={`${partnerWithExtras.fullName} foto`}
                  style={styles.userPhoto}
                  onError={handleImgError}
                />
                <span style={{ marginRight: 8 }}>{partnerWithExtras.emoji}</span>
                {partnerWithExtras.fullName}
              </div>
            </div>
          )}

          <ChatRoom room={room} />
        </>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: 20,
    backgroundImage: 'url("/logo/Zodiacus.jpeg")',
    backgroundSize: 'cover',
    backgroundColor: 'transparent',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    filter: 'brightness(1.05) contrast(1.1)',
    color: '#fff',
    minHeight: '100vh',
    position: 'relative',
    fontFamily: "'Poppins', sans-serif",
  },
  title: {
    fontSize: '2rem',
    color: '#FFFFFF',
    fontWeight: '700',
    fontStyle: 'italic',
    textAlign: 'center',
    textShadow: '2px 2px 6px rgba(0, 0, 0, 0.85)',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    padding: '10px 20px',
    borderRadius: 12,
    marginBottom: 20,
    maxWidth: 500,
    marginLeft: 'auto',
    marginRight: 'auto',
  },
  usersBox: {
    alignSelf: 'flex-start',
    backgroundColor: 'rgba(0, 0, 0, 0.45)',
    padding: '10px 15px',
    borderRadius: 10,
    color: '#fff',
    maxWidth: 300,
    marginBottom: 20,
  },
  usersTitle: {
    fontSize: '1rem',
    fontWeight: 'bold',
    color: '#DAA520',
    marginBottom: 8,
  },
  userItem: {
    fontSize: '0.95rem',
    marginBottom: 6,
    color: '#fff',
    fontWeight: '500',
    display: 'flex',
    alignItems: 'center',
  },
  userPhoto: {
    width: 32,
    height: 32,
    borderRadius: '50%',
    marginRight: 8,
    objectFit: 'cover',
    verticalAlign: 'middle',
    border: '2px solid #DAA520',
  },
  infoBox: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    color: '#fff',
    padding: 20,
    borderRadius: 12,
    maxWidth: 500,
    margin: '40px auto 0 auto',
    textAlign: 'center',
    boxShadow: '0 4px 12px rgba(0,0,0,0.3)',
  },
  infoTitle: {
    fontSize: '1.4rem',
    fontWeight: 'bold',
    color: '#DAA520',
    marginBottom: 10,
  },
  infoText: {
    fontSize: '1rem',
    fontStyle: 'italic',
    lineHeight: 1.5,
  },
  chatBox: {
    display: 'flex',
    flexDirection: 'column',
    border: '2px solid #DAA520',
    padding: 16,
    height: 420,
    overflowY: 'auto',
    marginBottom: 20,
    backgroundColor: 'rgba(255, 255, 255, 0.95)',
    color: '#333',
    borderRadius: 12,
    boxShadow: '0 8px 16px rgba(0, 0, 0, 0.25)',
  },
  messageItem: {
    display: 'flex',
    alignItems: 'flex-start',
    gap: 12,
    marginBottom: 14,
    padding: 12,
    borderRadius: 12,
    background: 'linear-gradient(135deg, #fdfcfb 0%, #e2d1c3 100%)',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    transition: 'background-color 0.3s ease',
  },
  messageUserName: {
    fontWeight: '700',
    textShadow: '1px 1px 4px rgba(0,0,0,0.25)',
    color: '#4B0082',
    marginBottom: 4,
  },
  messageText: {
    fontSize: '0.95rem',
    color: '#333',
  },
  messageTime: {
    fontSize: '0.75rem',
    color: '#666',
    marginTop: 6,
  },
  messagePhoto: {
    width: 36,
    height: 36,
    borderRadius: '50%',
    objectFit: 'cover',
    border: '2px solid #DAA520',
  },
  likeContainer: {
    display: 'flex',
    alignItems: 'center',
    marginLeft: 12,
    gap: 6,
  },
  likeButton: {
    fontSize: '1.4rem',
    background: 'transparent',
    border: 'none',
    cursor: 'pointer',
    padding: 0,
    lineHeight: 1,
    userSelect: 'none',
  },
  likeCount: {
    fontSize: '0.9rem',
    color: '#4B0082',
    fontWeight: '600',
    userSelect: 'none',
  },
  form: {
    display: 'flex',
    alignItems: 'center',
    gap: 8,
    marginTop: 20,
  },
  input: {
    flex: 1,
    padding: 10,
    fontSize: '1rem',
    borderRadius: 10,
    border: '2px solid #DAA520',
    outline: 'none',
    backgroundColor: 'white',
    color: '#4B0082',
    fontWeight: '600',
    boxShadow: '0 3px 8px rgba(0, 0, 0, 0.25)',
    transition: 'border-color 0.3s ease',
  },
  sendButton: {
    padding: '10px 20px',
    backgroundColor: '#DAA520',
    color: '#4B0082',
    border: 'none',
    borderRadius: 10,
    fontWeight: 'bold',
    cursor: 'pointer',
    boxShadow: '0 3px 8px rgba(218, 165, 32, 0.8)',
    transition: 'background-color 0.3s ease',
  },
  backButton: {
    position: 'absolute',
    top: 20,
    left: 20,
    backgroundColor: '#DAA520',
    color: '#4B0082',
    border: 'none',
    borderRadius: 8,
    padding: '10px 15px',
    fontWeight: 'bold',
    cursor: 'pointer',
    zIndex: 10,
  },
};

export default ZodiacRouletteRoom;
