import React, { useEffect, useState, useRef, useMemo } from 'react';
import { io } from 'socket.io-client';
import { useNavigate } from 'react-router-dom';

const SOCKET_SERVER_URL = 'http://localhost:5001';
const BASE_URL = 'http://localhost:5001/uploads/';

const signosEmoji = {
  aries: '♈', tauro: '♉', geminis: '♊', cancer: '♋', leo: '♌',
  virgo: '♍', libra: '♎', escorpio: '♏', sagitario: '♐',
  capricornio: '♑', acuario: '♒', piscis: '♓'
};

const ChatRoom = ({ room }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [usersRaw, setUsersRaw] = useState([]);
  const chatEndRef = useRef(null);
  const socketRef = useRef();
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const fullName = storedUser?.fullName || 'Anónimo';
    const zodiacSign = storedUser?.zodiacSign || '';

    socketRef.current = io(SOCKET_SERVER_URL, {
      auth: { token },
    });

    socketRef.current.emit('joinRoom', { room, fullName, zodiacSign });

    socketRef.current.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
      setTimeout(() => {
        chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
      }, 50);
    });

    socketRef.current.on('roomUsers', (usersList) => {
      setUsersRaw(usersList);
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, [room]);

  // Memoizar usuarios con URLs codificadas para evitar re-renderizados infinitos
  const users = useMemo(() => {
    return usersRaw.map(u => {
      const cleanSign = (u.zodiacSign || '')
        .toLowerCase()
        .normalize('NFD')
        .replace(/[\u0300-\u036f]/g, '');

      let photoPath = u.photoPath || '';
      if (photoPath && !photoPath.startsWith('http')) {
        // Asegurarse de que no se duplique /uploads en la ruta final
        if (photoPath.startsWith('uploads/')) {
          photoPath = photoPath.replace(/^uploads\//, '');
        } else if (photoPath.startsWith('/uploads/')) {
          photoPath = photoPath.replace(/^\/uploads\//, '');
        }
        photoPath = `${BASE_URL}${photoPath}`;
      }

      return {
        ...u,
        emoji: signosEmoji[cleanSign] || '',
        photoPath: photoPath || '/default-profile.png',
      };
    });
  }, [usersRaw]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    socketRef.current.emit('chatMessage', {
      room,
      text: message,
    });

    setMessage('');
  };

  const handleBack = () => {
    navigate(-1);
  };

  // Evitar recarga infinita de imágenes con error
  const handleImgError = (e) => {
    e.currentTarget.onerror = null;
    if (!e.currentTarget.dataset.fallback) {
      e.currentTarget.dataset.fallback = 'true';
      e.currentTarget.src = '/default-profile.png';
    }
  };

  return (
    <div style={styles.container}>
      <button onClick={handleBack} style={styles.backButton}>
        ← Volver
      </button>

      <h2 style={styles.title}>
        Bienvenido a la sala: {room.charAt(0).toUpperCase() + room.slice(1)}
      </h2>

      <div style={styles.usersBox}>
        <h4 style={styles.usersTitle}>Usuarios conectados:</h4>
        {users.length === 0 ? (
          <p style={styles.usersText}>No hay usuarios</p>
        ) : (
          <ul style={styles.usersList}>
            {users.map((u) => (
              <li key={u._id || u.id || u.fullName} style={styles.userItem}>
                <img
                  src={u.photoPath}
                  alt={`${u.fullName} foto`}
                  style={styles.userPhoto}
                  onError={handleImgError}
                />
                <span style={{ marginRight: 8 }}>{u.emoji}</span>
                {u.fullName}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div style={styles.chatBox}>
        {messages.map((msg, i) => {
          const user = users.find(u => u.fullName === msg.user);
          const emoji = user ? user.emoji : '';
          const photo = user ? user.photoPath : '/default-profile.png';
          return (
            <div key={i} style={styles.messageItem}>
              <img
                src={photo}
                alt={`${msg.user} foto`}
                style={styles.messagePhoto}
                onError={handleImgError}
              />
              <b>{msg.user || 'Anon'}</b> {emoji && <span style={{ marginLeft: 5 }}>{emoji}</span>}: {msg.text || '(sin texto)'}{' '}
              <small>({new Date(msg.timestamp).toLocaleTimeString()})</small>
            </div>
          );
        })}
        <div ref={chatEndRef} />
      </div>

      <form onSubmit={sendMessage} style={styles.form}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Escribí tu mensaje..."
          style={styles.input}
          autoComplete="off"
        />
        <button type="submit" style={styles.sendButton}>
          Enviar
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: 20,
    backgroundImage: 'url("/logo/Zodiacus.jpeg")',
    backgroundSize: 'cover',
    backgroundColor: 'transparent',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    filter: 'brightness(1.05) contrast(1.1)',
    color: '#fff',
    minHeight: '100vh',
    position: 'relative',
  },
  title: {
    fontSize: '2rem',
    color: '#FFFFFF',
    fontWeight: '700',
    fontStyle: 'italic',
    fontFamily: "'Poppins', sans-serif",
    textAlign: 'center',
    textShadow: '2px 2px 6px rgba(0, 0, 0, 0.85)',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    padding: '10px 20px',
    borderRadius: 12,
    marginBottom: 20,
    maxWidth: 500,
    marginLeft: 'auto',
    marginRight: 'auto',
  },
  chatBox: {
    border: '1px solid #ccc',
    padding: 10,
    height: 400,
    overflowY: 'scroll',
    marginBottom: 10,
    backgroundColor: '#fff',
    color: '#000',
    borderRadius: 10,
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
  },
  form: {
    display: 'flex',
    alignItems: 'center',
    gap: 8,
    marginTop: 20,
  },
  input: {
    flex: 1,
    padding: 8,
    fontSize: '1rem',
    borderRadius: 8,
    border: '2px solid #DAA520',
    outline: 'none',
    backgroundColor: 'white',
    color: '#4B0082',
    fontWeight: '500',
    boxShadow: '0 2px 6px rgba(0, 0, 0, 0.2)',
    transition: 'border-color 0.3s ease',
  },
  sendButton: {
    padding: '8px 16px',
    backgroundColor: '#DAA520',
    color: '#4B0082',
    border: 'none',
    borderRadius: 8,
    fontWeight: 'bold',
    cursor: 'pointer',
  },
  backButton: {
    position: 'absolute',
    top: 20,
    left: 20,
    backgroundColor: '#DAA520',
    color: '#4B0082',
    border: 'none',
    borderRadius: 8,
    padding: '10px 15px',
    fontWeight: 'bold',
    cursor: 'pointer',
    zIndex: 10,
  },
  usersBox: {
    alignSelf: 'flex-start',
    backgroundColor: 'rgba(0, 0, 0, 0.4)',
    padding: '10px 15px',
    borderRadius: 10,
    color: '#fff',
    maxWidth: 300,
    fontFamily: "'Poppins', sans-serif",
    marginBottom: 20,
  },
  usersTitle: {
    fontSize: '1rem',
    fontWeight: 'bold',
    color: '#DAA520',
    marginBottom: 8,
  },
  usersList: {
    listStyle: 'none',
    paddingLeft: 0,
    margin: 0,
  },
  userItem: {
    fontSize: '0.95rem',
    marginBottom: 4,
    color: '#fff',
    fontWeight: '500',
    display: 'flex',
    alignItems: 'center',
  },
  usersText: {
    fontSize: '0.9rem',
    color: '#ccc',
  },
  userPhoto: {
    width: 30,
    height: 30,
    borderRadius: '50%',
    marginRight: 8,
    objectFit: 'cover',
    verticalAlign: 'middle',
  },
  messagePhoto: {
    width: 24,
    height: 24,
    borderRadius: '50%',
    marginRight: 6,
    objectFit: 'cover',
    verticalAlign: 'middle',
  },
  messageItem: {
    display: 'flex',
    alignItems: 'center',
    gap: 6,
    marginBottom: 8,
  },
};

export default ChatRoom;
