import React, { useState, useContext, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';

const ProfileScreen = () => {
  const { user, refreshProfile } = useContext(AuthContext);

  const [fullName, setFullName] = useState('');
  const [bio, setBio] = useState('');
  const [photo, setPhoto] = useState(null);
  const [message, setMessage] = useState('');
  const [photoPreview, setPhotoPreview] = useState('');

  useEffect(() => {
    if (user) {
      setFullName(user.fullName || '');
      setBio(user.bio || '');
      setPhotoPreview(user.photoPath ? `http://localhost:5001/${user.photoPath}` : '');
    }
  }, [user]);

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    setPhoto(file);
    if (file) {
      setPhotoPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setMessage('No autorizado');
        return;
      }

      const formData = new FormData();
      formData.append('fullName', fullName);
      formData.append('bio', bio);
      if (photo) {
        formData.append('photo', photo);
      }

      const res = await fetch('http://localhost:5001/api/auth/profile', {
        method: 'PUT',
        headers: {
          Authorization: `Bearer ${token}`,
        },
        body: formData,
      });

      if (res.ok) {
        setMessage('Perfil actualizado correctamente.');
        await refreshProfile();
      } else {
        setMessage('Error al actualizar perfil.');
      }
    } catch (error) {
      setMessage('Error de conexión con el servidor.');
    }
  };

  return (
    <div style={{ padding: 20, backgroundColor: '#4B0082', minHeight: '100vh', color: '#fff' }}>
      <h2 style={{ color: '#DAA520' }}>Mi Perfil</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', maxWidth: 400 }}>
        <label style={{ marginBottom: 15 }}>
          Nombre completo:<br />
          <input
            style={{
              marginTop: 5,
              padding: 8,
              borderRadius: 6,
              border: 'none',
              fontSize: 16,
              width: '100%',
            }}
            type="text"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
        </label>

        <label style={{ marginBottom: 15 }}>
          Bio:<br />
          <textarea
            placeholder="Cuéntanos sobre vos"
            style={{
              marginTop: 5,
              padding: 8,
              borderRadius: 6,
              border: 'none',
              fontSize: 16,
              width: '100%',
              height: 80,
              resize: 'vertical',
            }}
            value={bio}
            onChange={(e) => setBio(e.target.value)}
          />
        </label>

        <label style={{ marginBottom: 15 }}>
          Foto de perfil:<br />
          <input
            style={{ marginTop: 5 }}
            type="file"
            accept="image/*"
            onChange={handlePhotoChange}
          />
        </label>

        {photoPreview && (
          <img
            src={photoPreview}
            alt="Foto de perfil"
            style={{ width: 120, height: 120, borderRadius: '50%', marginTop: 10, objectFit: 'cover' }}
          />
        )}

        <button
          type="submit"
          style={{
            marginTop: 20,
            padding: '10px 15px',
            backgroundColor: '#DAA520',
            color: '#4B0082',
            border: 'none',
            borderRadius: 10,
            fontWeight: 'bold',
            fontSize: 16,
            cursor: 'pointer',
          }}
        >
          Actualizar Perfil
        </button>
      </form>

      {message && (
        <p style={{ marginTop: 15, color: '#DAA520', fontWeight: 'bold' }}>
          {message}
        </p>
      )}
    </div>
  );
};

export default ProfileScreen;
