import { useState } from 'react';
import Layout from '../components/Layout';
import Button from '../components/Button';
import colors from '../styles/colors';
import { getZodiacSign } from '../utils/zodiacCompatibility';

const RegisterScreen = () => {
  const [fullName, setFullName] = useState('');
  const [dob, setDob] = useState('');
  const [dniFile, setDniFile] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validaciones mínimas
    if (!fullName || !dob || !dniFile) {
        alert('Por favor completá todos los campos');
        const signo = getZodiacSign(dob);
alert(`Tu signo es: ${signo}`);
      return;
    }

    // Acá iría la lógica para enviar los datos al backend
    alert('Registro enviado con éxito. (Faltan implementar conexiones)');
  };

  return (
    <Layout>
      <h2 style={{ color: colors.secondary }}>Registro de Usuario</h2>
      <form onSubmit={handleSubmit} style={{ maxWidth: 400 }}>
        <div style={{ marginBottom: 10 }}>
          <label>Nombre completo:</label><br />
          <input
            type="text"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            required
            style={{ width: '100%', padding: '8px' }}
          />
        </div>

        <div style={{ marginBottom: 10 }}>
          <label>Fecha de nacimiento:</label><br />
          <input
            type="date"
            value={dob}
            onChange={(e) => setDob(e.target.value)}
            required
            style={{ width: '100%', padding: '8px' }}
          />
        </div>

        <div style={{ marginBottom: 10 }}>
          <label>Subí tu DNI (PDF o imagen):</label><br />
          <input
            type="file"
            accept="image/*,.pdf"
            onChange={(e) => setDniFile(e.target.files[0])}
            required
          />
        </div>

        <Button>Registrarme</Button>
      </form>
    </Layout>
  );
};

export default RegisterScreen;
