import React, { useEffect, useState, useRef, useMemo } from 'react';
import { io } from 'socket.io-client';
import { useNavigate } from 'react-router-dom';
import EmojiPicker from 'emoji-picker-react';

const SOCKET_SERVER_URL = 'http://localhost:5001';
const BASE_URL = 'http://localhost:5001/uploads/';

const signosEmoji = {
  aries: '♈', tauro: '♉', geminis: '♊', cancer: '♋', leo: '♌',
  virgo: '♍', libra: '♎', escorpio: '♏', sagitario: '♐',
  capricornio: '♑', acuario: '♒', piscis: '♓'
};

const ChatRoom = ({ room }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [usersRaw, setUsersRaw] = useState([]);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [likedMessages, setLikedMessages] = useState(new Set());

  const chatEndRef = useRef(null);
  const socketRef = useRef();
  const navigate = useNavigate();

  const storedUser = JSON.parse(localStorage.getItem('user')) || {};
  const fullName = storedUser.fullName || 'Anónimo';
  const zodiacSign = storedUser.zodiacSign || '';
  const userPhotoPath = storedUser.photoPath || '';

  useEffect(() => {
    const token = localStorage.getItem('token');

    socketRef.current = io(SOCKET_SERVER_URL, {
      auth: { token },
    });

    socketRef.current.emit('joinRoom', { room, fullName, zodiacSign, photoPath: userPhotoPath });

    socketRef.current.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
      setTimeout(() => {
        chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
      }, 50);
    });

    socketRef.current.on('roomUsers', (usersList) => {
      setUsersRaw(usersList);
    });

    socketRef.current.on('likesUpdate', ({ messageId, likesCount }) => {
      setMessages((prevMessages) =>
        prevMessages.map((m) =>
          m.id === messageId ? { ...m, likesCount } : m
        )
      );
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, [room, fullName, zodiacSign, userPhotoPath]);

  const users = useMemo(() => {
    return usersRaw.map(u => {
      const cleanSign = (u.zodiacSign || '')
        .toLowerCase()
        .normalize('NFD')
        .replace(/[\u0300-\u036f]/g, '');

      let photoPath = u.photoPath || '';

      if (photoPath && !photoPath.startsWith('http')) {
        const cleanedPath = photoPath.replace(/^\/?uploads\//, '');
        photoPath = `${BASE_URL}${cleanedPath}`;
      }

      return {
        ...u,
        emoji: signosEmoji[cleanSign] || '',
        photoPath: photoPath || '/default-profile.png',
      };
    });
  }, [usersRaw]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    socketRef.current.emit('chatMessage', {
      room,
      text: message.trim().replace(/[\n\r\\]+/g, ''),
    });

    setMessage('');
    setShowEmojiPicker(false);
  };

  const handleBack = () => {
    navigate(-1);
  };

  const handleImgError = (e) => {
    e.currentTarget.onerror = null;
    e.currentTarget.src = '/default-profile.png';
  };

  const toggleLike = (messageId) => {
    if (!socketRef.current) return;

    const newLikedMessages = new Set(likedMessages);
    if (newLikedMessages.has(messageId)) {
      newLikedMessages.delete(messageId);
    } else {
      newLikedMessages.add(messageId);
    }
    setLikedMessages(newLikedMessages);

    socketRef.current.emit('toggleLike', { room, messageId });
  };

  const addEmoji = (emojiData) => {
    setMessage((prev) => prev + emojiData.emoji);
  };

  return (
    <div style={styles.container}>
      <button onClick={handleBack} style={styles.backButton}>
        ← Volver
      </button>

      <h2 style={styles.title}>
        Bienvenido a la sala: {room.charAt(0).toUpperCase() + room.slice(1)}
      </h2>

      <div style={styles.usersBox}>
        <h4 style={styles.usersTitle}>Usuarios conectados:</h4>
        {users.length === 0 ? (
          <p style={styles.usersText}>No hay usuarios</p>
        ) : (
          <ul style={styles.usersList}>
            {users.map((u) => (
              <li key={u._id || u.id || u.fullName} style={styles.userItem}>
                <img
                  src={u.photoPath}
                  alt={`${u.fullName} foto`}
                  style={styles.userPhoto}
                  onError={handleImgError}
                />
                <span style={{ marginRight: 8 }}>{u.emoji}</span>
                {u.fullName}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div style={styles.chatBox}>
        {messages.map((msg) => {
          const user = users.find(u => u.fullName === msg.user);
          const emoji = user ? user.emoji : '';
          const photo = user ? user.photoPath : '/default-profile.png';
          const liked = likedMessages.has(msg.id);
          return (
            <div key={msg.id} style={styles.messageItem}>
              <img
                src={photo}
                alt={`${msg.user} foto`}
                style={styles.messagePhoto}
                onError={handleImgError}
              />
              <div style={{ flexGrow: 1, marginLeft: 10 }}>
                <div style={{ fontWeight: 'bold', textShadow: '1px 1px 2px rgba(0,0,0,0.2)', fontSize: '1.05rem' }}>
                  {msg.user || 'Anon'} {emoji && <span style={{ marginLeft: 5 }}>{emoji}</span>}
                </div>
                <div style={{ fontSize: '0.95rem', marginTop: 4 }}>{msg.text || '(sin texto)'}</div>
                <div style={{ fontSize: '0.75rem', color: '#666', marginTop: 2 }}>
                  {new Date(msg.timestamp).toLocaleTimeString()}
                </div>
              </div>
              <button
                onClick={() => toggleLike(msg.id)}
                style={styles.likeButton}
                aria-label={liked ? 'Quitar like' : 'Dar like'}
                title={liked ? 'Quitar like' : 'Dar like'}
              >
                <span style={{ color: liked ? '#e0245e' : '#999', fontSize: '1.5rem' }}>❤️</span>
                <span style={styles.likeCount}>{msg.likesCount || 0}</span>
              </button>
            </div>
          );
        })}
        <div ref={chatEndRef} />
      </div>

      <div style={{ position: 'relative', marginBottom: 10 }}>
        <button
          type="button"
          onClick={() => setShowEmojiPicker((prev) => !prev)}
          style={{
            fontSize: '1.5rem',
            background: 'transparent',
            border: 'none',
            cursor: 'pointer',
            marginBottom: 5,
          }}
          aria-label="Mostrar selector de emojis"
        >
          😀
        </button>
        {showEmojiPicker && (
          <div style={{ position: 'absolute', zIndex: 100 }}>
            <EmojiPicker onEmojiClick={addEmoji} />
          </div>
        )}
      </div>

      <form onSubmit={sendMessage} style={styles.form}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Escribí tu mensaje..."
          style={styles.input}
          autoComplete="off"
        />
        <button type="submit" style={styles.sendButton}>
          Enviar
        </button>
      </form>
    </div>
  );
};

