import React, { useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

const WelcomeScreen = () => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  useEffect(() => {
    if (user) {
      navigate("/home");
    }
  }, [user, navigate]);

  return (
    <div
      style={{
        height: "100vh",
        backgroundColor: "#4B0082",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        padding: "2rem",
        color: "#fff",
        textAlign: "center",
      }}
    >
      <h1 style={{ fontSize: "3rem", marginBottom: "1.5rem", color: "#fff" }}>
        Zodiacus
      </h1>
      <p style={{ fontSize: "1.2rem", marginBottom: "2rem" }}>
        Conectá con personas según su signo zodiacal
      </p>

      <div
        style={{
          display: "flex",
          flexDirection: "column",
          gap: "1rem",
          width: "100%",
          maxWidth: "300px",
        }}
      >
        <button
          onClick={() => navigate("/login")}
          style={{
            backgroundColor: "#DAA520",
            color: "#4B0082",
            padding: "0.8rem",
            border: "none",
            borderRadius: "8px",
            fontSize: "1rem",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          Iniciar Sesión
        </button>

        <button
          onClick={() => navigate("/register")}
          style={{
            backgroundColor: "#DAA520",
            color: "#4B0082",
            padding: "0.8rem",
            border: "none",
            borderRadius: "8px",
            fontSize: "1rem",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          Registrarse
        </button>
      </div>
    </div>
  );
};

export default WelcomeScreen;
