import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';

const SOCKET_SERVER_URL = 'http://localhost:5001'; // URL backend
// const SOCKET_SERVER_URL = 'http://172.20.10.5:5001';

const ChatRoom = ({ room }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);
  const socketRef = useRef();
  const userRef = useRef(null); // Se define fuera del useEffect

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const fullName = storedUser?.fullName || 'Anónimo';
    userRef.current = fullName;

    socketRef.current = io(SOCKET_SERVER_URL, {
      auth: { token },
    });

    socketRef.current.emit('joinRoom', { room, fullName });

    socketRef.current.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    socketRef.current.on('roomUsers', (usersList) => {
      setUsers(usersList);
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, [room]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    const newMessage = {
      room,
      text: message,
      user: userRef.current,
      timestamp: new Date().toISOString(),
    };

    socketRef.current.emit('chatMessage', newMessage);
    setMessage('');
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Sala: {room.charAt(0).toUpperCase() + room.slice(1)}</h2>

      <h4>Usuarios conectados:</h4>
      {users.length === 0 ? (
        <p>No hay usuarios conectados</p>
      ) : (
        <ul>
          {users.map((u) => (
            <li key={u.id}>{u.fullName}</li>
          ))}
        </ul>
      )}

      <div
        style={{
          border: '1px solid #ccc',
          padding: 10,
          height: 300,
          overflowY: 'scroll',
          marginBottom: 10,
        }}
      >
        {messages.map((msg, i) => (
          <div key={i}>
            <b>{msg.user || 'Anon'}:</b> {msg.text}{' '}
            <small>({new Date(msg.timestamp).toLocaleTimeString()})</small>
          </div>
        ))}
      </div>

      <form onSubmit={sendMessage}>
        <input
          name="chatMessage"
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Escribí tu mensaje..."
          style={{ width: '80%', padding: 8, marginRight: 8 }}
        />
        <button type="submit" style={{ padding: '8px 16px' }}>
          Enviar
        </button>
      </form>
    </div>
  );
};

export default ChatRoom;
