import React, { useState } from 'react';
import axios from 'axios';
// import './LoginScreen.css'; // Podés eliminarlo si no usás CSS externo

const LoginScreen = ({ navigate }) => {
  const [fullName, setFullName] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!fullName || !password) {
      alert('Por favor ingresa tu nombre y contraseña.');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5001/api/auth/login', {
        fullName,
        password,
      });

      const { message, token, userId } = response.data;
      alert(message);

      // Redirigir al usuario a la pantalla de salas
      navigate('/salas', { state: { token, userId } });
    } catch (err) {
      console.error(err);
      alert('Credenciales inválidas o error del servidor.');
    }
  };

  return (
    <div className="login-container">
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Nombre completo"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Ingresar</button>
      </form>
    </div>
  );
};

export default LoginScreen;
