import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function LoginScreen() {
  const navigate = useNavigate();
  const [dni, setDni] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await fetch('http://localhost:5001/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ dni, password }),
      });

      const data = await response.json();

      if (response.ok) {
        console.log('Inicio de sesión exitoso:', data);
        localStorage.setItem('userId', data.userId);
        localStorage.setItem('zodiacSign', data.zodiacSign);
        navigate('/salas'); // Redirige al selector de salas
      } else {
        setError(data.message || 'Error al iniciar sesión');
      }
    } catch (err) {
      console.error('Error al iniciar sesión:', err);
      setError('Ocurrió un error en el servidor');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-[#4B0082] to-[#DAA520] flex items-center justify-center">
      <form
        onSubmit={handleLogin}
        className="bg-white p-6 rounded-xl shadow-lg w-full max-w-sm"
      >
        <h2 className="text-2xl font-bold mb-4 text-center text-[#4B0082]">
          Iniciar Sesión
        </h2>

        {error && (
          <p className="text-red-500 text-sm mb-4 text-center">{error}</p>
        )}

        <div className="mb-4">
          <label className="block text-gray-700 mb-1">DNI</label>
          <input
            type="text"
            name="dni"
            value={dni}
            onChange={(e) => setDni(e.target.value)}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-[#4B0082]"
            placeholder="Ingrese su DNI"
            required
          />
        </div>

        <div className="mb-6">
          <label className="block text-gray-700 mb-1">Contraseña</label>
          <input
            type="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:border-[#4B0082]"
            placeholder="Ingrese su contraseña"
            required
          />
        </div>

        <button
          type="submit"
          className="w-full bg-[#4B0082] text-white py-2 rounded-lg hover:bg-[#3a006c] transition"
        >
          Iniciar Sesión
        </button>
      </form>
    </div>
  );
}

export default LoginScreen;
