// screens/LoginScreen.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // para navegación en React web
import './LoginScreen.css'; // si tenés tu propio CSS

const LoginScreen = () => {
  const navigation = useNavigation();

  const [fullName, setFullName] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    if (!fullName || !password) {
      setError('Por favor completa todos los campos.');
      return;
    }

    try {
      const response = await fetch('http://10.0.2.2:5001/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ fullName, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        setError(data.message || 'Error al iniciar sesión.');
      } else {
        setError('');
        navigation.navigate('ZodiacRooms', { userId: data.userId });
      }
    } catch (error) {
      console.error('Error en login:', error);
      setError('Error de red. Verificá la conexión con el servidor.');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Iniciar Sesión</Text>

      <TextInput
        placeholder="Nombre completo"
        value={fullName}
        onChangeText={setFullName}
        style={styles.input}
      />

      <TextInput
        placeholder="Contraseña"
        secureTextEntry
        value={password}
        onChangeText={setPassword}
        style={styles.input}
      />

      {error ? <Text style={styles.error}>{error}</Text> : null}

      <Button title="Ingresar" onPress={handleLogin} />

      <Text
        style={styles.registerLink}
        onPress={() => navigation.navigate('Register')}
      >
        ¿No tenés cuenta? Registrate
      </Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    padding: 20,
    backgroundColor: '#fff',
  },
  title: {
    fontSize: 24,
    marginBottom: 20,
    textAlign: 'center',
    color: '#4B0082',
  },
  input: {
    borderWidth: 1,
    borderColor: '#999',
    padding: 10,
    marginBottom: 15,
    borderRadius: 5,
  },
  error: {
    color: 'red',
    marginBottom: 10,
  },
  registerLink: {
    marginTop: 15,
    color: '#4B0082',
    textAlign: 'center',
  },
});

export default LoginScreen;
