import React, { useState } from 'react';
import axios from 'axios';
import './LoginScreen.css'; // Opcional: estilos externos

const LoginScreen = ({ navigate }) => {
  const [dni, setDni] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!dni || !password) {
      alert('Por favor ingresa DNI y contraseña.');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('dni', dni);
      formData.append('password', password);

      const response = await axios.post('http://localhost:5001/api/auth/login', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      const { message, token, userId } = response.data;
      alert(message);

      // Navegación (ajustá según tu sistema de rutas)
      navigate('/salas', { state: { token, userId } });
    } catch (err) {
      console.error(err);
      alert('Credenciales inválidas o error del servidor.');
    }
  };

  return (
    <div className="login-container">
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="DNI"
          value={dni}
          onChange={(e) => setDni(e.target.value)}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Ingresar</button>
      </form>
    </div>
  );
};

export default LoginScreen;
