import React from 'react';
import { useNavigate } from 'react-router-dom';
import colors from '../styles/colors'; // tus colores definidos

const signos = [
  'Aries', 'Tauro', 'Géminis', 'Cáncer', 'Leo', 'Virgo',
  'Libra', 'Escorpio', 'Sagitario', 'Capricornio', 'Acuario', 'Piscis'
];

function ZodiacRoomsScreen() {
  const navigate = useNavigate();

  const handleClick = (signo) => {
    navigate(`/rooms/${signo.toLowerCase()}`);
  };

  return (
    <div style={{ padding: 20 }}>
      <h2 style={{ color: colors.primary }}>Salas por Signo Zodiacal</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 15 }}>
        {signos.map((signo) => (
          <button
            key={signo}
            onClick={() => handleClick(signo)}
            style={{
              backgroundColor: colors.primary,
              color: colors.secondary,
              border: 'none',
              borderRadius: 8,
              padding: '15px 25px',
              cursor: 'pointer',
              fontWeight: 'bold',
              fontSize: '1rem',
              flex: '1 0 30%',
              minWidth: 120,
              transition: 'background-color 0.3s',
            }}
            onMouseEnter={e => e.currentTarget.style.backgroundColor = colors.secondary}
            onMouseLeave={e => e.currentTarget.style.backgroundColor = colors.primary}
          >
            {signo}
          </button>
        ))}
      </div>
    </div>
  );
}

export default ZodiacRoomsScreen;
