import React, { useEffect, useState, useMemo, useRef } from 'react';
import { io } from 'socket.io-client';
import { useNavigate } from 'react-router-dom';
import ChatRoom from '../components/ChatRoom';

const SOCKET_SERVER_URL = 'http://localhost:5001';

const signosEmoji = {
  aries: '‚ôà', tauro: '‚ôâ', geminis: '‚ôä', cancer: '‚ôã', leo: '‚ôå',
  virgo: '‚ôç', libra: '‚ôé', escorpio: '‚ôè', sagitario: '‚ôê',
  capricornio: '‚ôë', acuario: '‚ôí', piscis: '‚ôì'
};

const ZodiacRouletteRoom = () => {
  const [partner, setPartner] = useState(null);
  const [room, setRoom] = useState(null);
  const [waiting, setWaiting] = useState(true);
  const socketRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const token = localStorage.getItem('token');
    if (!storedUser || !token) return;

    socketRef.current = io(SOCKET_SERVER_URL, { auth: { token } });

    socketRef.current.emit('joinRoulette', storedUser);

    socketRef.current.on('roulettePartner', (data) => {
      setPartner(data);
      setRoom(data.room);
      setWaiting(false);
    });

    socketRef.current.on('waitingPartner', () => {
      setWaiting(true);
      setPartner(null);
      setRoom(null);
    });

    return () => {
      socketRef.current.disconnect();
      setPartner(null);
      setRoom(null);
      setWaiting(true);
    };
  }, []);

  const handleBack = () => {
    navigate(-1);
  };

  const partnerWithExtras = useMemo(() => {
    if (!partner) return null;
    const cleanSign = (partner.zodiacSign || '')
      .toLowerCase()
      .normalize('NFD')
      .replace(/[\u0300-\u036f]/g, '');

    return {
      ...partner,
      emoji: signosEmoji[cleanSign] || '',
    };
  }, [partner]);

  return (
    <div style={{ padding: 20, position: 'relative', minHeight: '100vh', backgroundColor: '#1a001a' }}>
      <button
        onClick={handleBack}
        style={{
          position: 'absolute',
          top: 20,
          left: 20,
          backgroundColor: '#DAA520',
          color: '#4B0082',
          border: 'none',
          borderRadius: 8,
          padding: '10px 15px',
          fontWeight: 'bold',
          cursor: 'pointer',
          zIndex: 10,
        }}
      >
        ‚Üê Volver
      </button>

      <h2 style={{ color: '#DAA520', textAlign: 'center', marginBottom: 20 }}>
        üé≤ Ruleta Zodiacal üîÆ
      </h2>

      {waiting && (
        <div style={{
          backgroundColor: 'rgba(0, 0, 0, 0.5)',
          color: '#fff',
          padding: 20,
          borderRadius: 12,
          maxWidth: 500,
          margin: '40px auto',
          textAlign: 'center',
          boxShadow: '0 4px 12px rgba(0,0,0,0.3)',
        }}>
          <h3 style={{ color: '#DAA520' }}>Buscando usuario...</h3>
          <p>Ser√°s emparejado aleatoriamente con otro usuario para chatear.</p>
        </div>
      )}

      {!waiting && partnerWithExtras && (
        <div style={{
          backgroundColor: 'rgba(0, 0, 0, 0.45)',
          padding: '10px 15px',
          borderRadius: 10,
          color: '#fff',
          maxWidth: 300,
          margin: '20px auto',
          display: 'flex',
          alignItems: 'center',
          gap: 10,
        }}>
          <img
            src={partnerWithExtras.photoPath || '/default-profile.png'}
            alt={`${partnerWithExtras.fullName} foto`}
            style={{ width: 40, height: 40, borderRadius: '50%', border: '2px solid #DAA520', objectFit: 'cover' }}
            onError={(e) => { e.currentTarget.src = '/default-profile.png'; }}
          />
          <span style={{ fontSize: '1.5rem' }}>{partnerWithExtras.emoji}</span>
          <span style={{ fontWeight: 'bold', fontSize: '1.2rem' }}>{partnerWithExtras.fullName}</span>
        </div>
      )}

      {!waiting && room && (
        <ChatRoom room={room} />
      )}
    </div>
  );
};

export default ZodiacRouletteRoom;
