import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const LoginScreen = () => {
  const [fullName, setFullName] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5001/api/users/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ fullName, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        return setError(data.message || 'Error al iniciar sesión.');
      }

      // Guardamos la sesión
      await login(data.userId);

      // Buscamos el signo del usuario con su ID
      const userRes = await fetch(`http://localhost:5001/api/users/${data.userId}`);
      const userData = await userRes.json();

      if (!userRes.ok) {
        return setError('Error al obtener los datos del usuario.');
      }

      // Redirigir a la sala del signo
      navigate(`/rooms/${userData.zodiacSign}`);
    } catch (error) {
      console.error('Error en login:', error);
      setError('Ocurrió un error. Intenta de nuevo.');
    }
  };

  return (
    <div>
      <h2>Iniciar Sesión</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nombre completo"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Entrar</button>
      </form>
    </div>
  );
};

export default LoginScreen;
