import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';

const SOCKET_SERVER_URL = 'http://localhost:5001';

const ChatRoom = ({ room }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);
  const socketRef = useRef();

  useEffect(() => {
    const token = localStorage.getItem('token');

    socketRef.current = io(SOCKET_SERVER_URL, {
      auth: { token },
    });

    socketRef.current.on('connect_error', (err) => {
      console.error('Error conexión socket:', err.message);
    });

    // Escuchar mensajes entrantes
    socketRef.current.on('message', (msg) => {
      console.log('Mensaje recibido:', msg); // Para depurar
      setMessages((prev) => [...prev, msg]);
    });

    // Aquí podrías manejar usuarios conectados si implementas esa funcionalidad en backend
    // socketRef.current.on('roomUsers', (usersList) => {
    //   setUsers(usersList.map(u => u.user));
    // });

    return () => {
      socketRef.current.disconnect();
    };
  }, [room]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    // Enviar solo el mensaje, el usuario lo asigna backend desde el token
    socketRef.current.emit('chatMessage', { message });

    setMessage('');
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Sala: {room}</h2>
      <h4>Usuarios conectados:</h4>
      <ul>
        {users.length === 0 ? (
          <li>No hay usuarios conectados</li>
        ) : (
          users.map((u, i) => <li key={i}>{u.fullName}</li>)
        )}
      </ul>

      <div
        style={{
          border: '1px solid #ccc',
          padding: 10,
          height: 300,
          overflowY: 'scroll',
          marginBottom: 10,
        }}
      >
        {messages.map((msg, i) => (
          <div key={i}>
            <b>{msg.user || 'Anon'}:</b> {msg.text}{' '}
            <small>({new Date(msg.timestamp).toLocaleTimeString()})</small>
          </div>
        ))}
      </div>

      <form onSubmit={sendMessage}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Escribí tu mensaje..."
          style={{ width: '80%', padding: 8, marginRight: 8 }}
        />
        <button type="submit" style={{ padding: '8px 16px' }}>
          Enviar
        </button>
      </form>
    </div>
  );
};

export default ChatRoom;
