// screens/ZodiacRoomsScreen.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const ZodiacRoomsScreen = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        setUser(decoded);
      } catch (error) {
        console.error('Token inválido:', error);
        navigate('/login');
      }
    } else {
      navigate('/login');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.subtitle}>Bienvenido, {user?.fullName}</h2>
      <h1 style={styles.title}>Salas Zodiacales</h1>
      <div style={styles.grid}>
        {/* Aquí se mostrarán las salas zodiacales */}
      </div>
      <div style={styles.footer}>
        {/* Aquí podrías agregar más enlaces de navegación en el futuro */}
        <button style={styles.logoutButton} onClick={handleLogout}>
          Cerrar sesión
        </button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: 20,
    backgroundColor: '#282c34',
    minHeight: '100vh',
    color: 'white',
    textAlign: 'center',
  },
  title: {
    fontSize: '2.5rem',
    marginBottom: 20,
  },
  subtitle: {
    fontSize: '1.2rem',
    marginBottom: 10,
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
    gap: 20,
  },
  logoutButton: {
    backgroundColor: '#ff4d4d',
    border: 'none',
    padding: '10px 20px',
    color: 'white',
    fontSize: '1rem',
    cursor: 'pointer',
    borderRadius: 5,
  },
  footer: {
    marginTop: 40,
  },
};

export default ZodiacRoomsScreen;
