import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext'; // ajusta ruta si hace falta

const LoginScreen = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullName: '',
    birthDate: '',
    password: ''
  });

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };
    const { login } = React.useContext(AuthContext); // obtenemos la función de login del contexto

  const handleSubmit = async (e) => {
  e.preventDefault();

  try {
    const res = await fetch('http://localhost:5001/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        fullName: formData.fullName,
        dob: formData.birthDate,  // Ojo que en backend usas 'dob' (date of birth)
        password: formData.password,
      }),
    });

    const data = await res.json();

    if (!res.ok) {
      alert(data.error || 'Error en el login');
      return;
    }

    // Guardar usuario en contexto para marcar sesión iniciada
    login(data.user);

    // Redirigir a home o a salas
    navigate('/home');
  } catch (error) {
    alert('Error al conectar con el servidor');
    console.error(error);
  }
};

  return (
    <div style={{
      height: '100vh',
      backgroundColor: '#4B0082',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#fff',
      padding: '2rem'
    }}>
      <form onSubmit={handleSubmit} style={{
        backgroundColor: '#fff',
        padding: '2rem',
        borderRadius: '1rem',
        boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
        width: '100%',
        maxWidth: '400px',
        color: '#4B0082'
      }}>
        <h2 style={{ textAlign: 'center', marginBottom: '1.5rem', color: '#4B0082' }}>
          Iniciar Sesión
        </h2>

        <div style={{ marginBottom: '1rem' }}>
          <label>Nombre completo:</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        <div style={{ marginBottom: '1rem' }}>
          <label>Fecha de nacimiento:</label>
          <input
            type="date"
            name="birthDate"
            value={formData.birthDate}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        <div style={{ marginBottom: '1.5rem' }}>
          <label>Contraseña:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        <button type="submit" style={buttonStyle}>
          Entrar
        </button>
      </form>
    </div>
  );
};

const inputStyle = {
  width: '100%',
  padding: '0.8rem',
  border: '1px solid #ccc',
  borderRadius: '8px',
  fontSize: '1rem',
  marginTop: '0.3rem'
};

const buttonStyle = {
  width: '100%',
  padding: '0.8rem',
  backgroundColor: '#DAA520',
  color: '#4B0082',
  border: 'none',
  borderRadius: '8px',
  fontSize: '1rem',
  fontWeight: 'bold',
  cursor: 'pointer'
};

export default LoginScreen;
