import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const LoginScreen = () => {
  const [fullName, setFullName] = useState('');
  const [dob, setDob] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
  e.preventDefault();
  console.log('Intentando iniciar sesión con:', { fullName, dob, password });

  if (!fullName || !dob || !password) {
    setError('Por favor completá todos los campos.');
    return;
  }

  try {
    const res = await fetch('http://localhost:5001/api/auth/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ fullName, dob, password }),
    });

    console.log('Respuesta del backend status:', res.status);

    const data = await res.json();
    console.log('Respuesta del backend:', data);

    if (!res.ok) {
      setError(data.message || 'Error al iniciar sesión.');
      return;
    }

    localStorage.setItem('token', data.token);
    localStorage.setItem('user', JSON.stringify(data.user));

    login(data.user); // 👈 ESTA LÍNEA ES CLAVE

    console.log('Login exitoso, navegando a /home');
    navigate('/home');
  } catch (err) {
    console.error('Error en login:', err);
    setError('Error al conectar con el servidor.');
  }
};

  return (
    <div className="login-container">
      <h2>Iniciar Sesión</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>Nombre completo:</label>
          <input
            type="text"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
        </div>

        <div>
          <label>Fecha de nacimiento:</label>
          <input
            type="date"
            value={dob}
            onChange={(e) => setDob(e.target.value)}
          />
        </div>

        <div>
          <label>Contraseña:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>

        <button type="submit">Ingresar</button>
      </form>
    </div>
  );
};

export default LoginScreen;
