import React, { useEffect, useState, useRef, useContext } from 'react';
import { io } from 'socket.io-client';
import { AuthContext } from '../context/AuthContext'; // Asegurate que la ruta sea correcta

const SOCKET_SERVER_URL = 'http://localhost:5001';

const ChatRoom = ({ room }) => {
  const { user } = useContext(AuthContext); // Obtenemos el usuario autenticado
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);
  const socketRef = useRef();

  useEffect(() => {
    const token = localStorage.getItem('token');

    socketRef.current = io(SOCKET_SERVER_URL, {
      auth: { token },
    });

    socketRef.current.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    socketRef.current.on('roomUsers', (usersList) => {
      setUsers(usersList.map(u => u.user));
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, [room]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (!message.trim() || !user) return;

    socketRef.current.emit('chatMessage', {
      room,
      message,
      user,
    });

    setMessage('');
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Sala: {room}</h2>
      <h4>Usuarios conectados:</h4>
      <ul>
        {users.map((u, i) => (
          <li key={i}>{u?.fullName || 'Desconocido'}</li>
        ))}
      </ul>

      <div
        style={{
          border: '1px solid #ccc',
          padding: 10,
          height: 300,
          overflowY: 'scroll',
          marginBottom: 10,
        }}
      >
        {messages.map((msg, i) => (
          <div key={i}>
            <b>{msg.user?.fullName || msg.user || 'Anónimo'}:</b> {msg.text}{' '}
            <small>({new Date(msg.timestamp).toLocaleTimeString()})</small>
          </div>
        ))}
      </div>

      <form onSubmit={sendMessage}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Escribí tu mensaje..."
          style={{ width: '80%', padding: 8, marginRight: 8 }}
        />
        <button type="submit" style={{ padding: '8px 16px' }}>
          Enviar
        </button>
      </form>
    </div>
  );
};

export default ChatRoom;
