import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';

const LoginScreen = () => {
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);
  const [formData, setFormData] = useState({
    fullName: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!formData.fullName || !formData.password) {
      setError('Por favor complet√° todos los campos.');
      return;
    }

    try {
      setLoading(true);
      const res = await fetch('http://localhost:5001/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      const data = await res.json();
      setLoading(false);

      if (!res.ok) {
        setError(data.message || 'Error al iniciar sesi√≥n.');
        return;
      }

      // Guardar token y datos m√≠nimos en localStorage
      localStorage.setItem('token', data.token);
      localStorage.setItem('user', JSON.stringify({
        fullName: formData.fullName,
        userId: data.userId,
        zodiacSign: data.zodiacSign,
        photoPath: data.photoPath || '',
      }));

      login(data.token); // Actualiza el contexto global
      navigate('/rooms'); // Redirige a la pantalla de salas
    } catch (err) {
      setLoading(false);
      setError('Error de conexi√≥n con el servidor.');
    }
  };

  return (
    <div style={styles.container}>
      <form onSubmit={handleSubmit} style={styles.form}>
        <h2 style={styles.title}>Iniciar Sesi√≥n</h2>

        {error && <div style={styles.error}>{error}</div>}

        <label style={styles.label}>Nombre Completo</label>
        <input
          type="text"
          name="fullName"
          placeholder="Tu nombre completo"
          value={formData.fullName}
          onChange={handleChange}
          style={styles.input}
          autoComplete="off"
        />

        <label style={styles.label}>Contrase√±a</label>
        <div style={{ position: 'relative' }}>
          <input
            type={showPassword ? 'text' : 'password'}
            name="password"
            placeholder="Tu contrase√±a"
            value={formData.password}
            onChange={handleChange}
            style={styles.input}
            autoComplete="off"
          />
          <span
            onClick={() => setShowPassword(!showPassword)}
            style={styles.toggleIcon}
          >
            {showPassword ? 'üôà' : 'üëÅÔ∏è'}
          </span>
        </div>

        <button
          type="submit"
          disabled={loading}
          style={{
            ...styles.button,
            opacity: loading ? 0.6 : 1,
          }}
        >
          {loading ? 'Ingresando...' : 'Ingresar'}
        </button>

        <p style={styles.text}>
          ¬øNo ten√©s cuenta?{' '}
          <Link to="/register" style={styles.link}>
            Registrate ac√°
          </Link>
        </p>
      </form>
    </div>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #4B0082 0%, #120136 100%)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    padding: '5%',
  },
  form: {
    backgroundColor: '#fff',
    borderRadius: 15,
    padding: '40px 30px',
    width: '100%',
    maxWidth: 400,
    boxShadow: '0 8px 24px rgba(0,0,0,0.2)',
    color: '#4B0082',
    fontFamily: 'Segoe UI, Tahoma, Geneva, Verdana, sans-serif',
  },
  title: {
    textAlign: 'center',
    color: '#DAA520',
    marginBottom: 25,
    fontWeight: 'bold',
    fontSize: 28,
  },
  label: {
    display: 'block',
    marginBottom: 6,
    marginTop: 18,
    fontWeight: '600',
    fontSize: 14,
  },
  input: {
    width: '100%',
    padding: '10px 12px',
    borderRadius: 8,
    border: '2px solid #4B0082',
    fontSize: 16,
    outline: 'none',
    transition: 'border-color 0.3s ease',
  },
  button: {
    width: '100%',
    marginTop: 30,
    padding: '12px',
    borderRadius: 8,
    backgroundColor: '#DAA520',
    color: '#4B0082',
    fontWeight: 'bold',
    fontSize: 18,
    cursor: 'pointer',
    border: 'none',
    boxShadow: '0 4px 8px rgba(218, 165, 32, 0.6)',
    transition: 'background-color 0.3s ease',
  },
  text: {
    marginTop: 20,
    textAlign: 'center',
    fontSize: 14,
    color: '#555',
  },
  link: {
    color: '#DAA520',
    fontWeight: 'bold',
    textDecoration: 'none',
  },
  error: {
    backgroundColor: '#f44336',
    color: 'white',
    padding: '10px 15px',
    borderRadius: 8,
    textAlign: 'center',
    marginBottom: 15,
  },
  toggleIcon: {
    position: 'absolute',
    right: 10,
    top: '50%',
    transform: 'translateY(-50%)',
    cursor: 'pointer',
    color: '#DAA520',
    fontSize: 18,
  },
};

export default LoginScreen;
