import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';

const SOCKET_SERVER_URL = 'http://localhost:5001'; // tu backend

const ChatRoom = ({ user, room }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]); // mensajes del chat
  const [users, setUsers] = useState([]); // usuarios en la sala
  const socketRef = useRef();

  useEffect(() => {
    // Crear conexión
    socketRef.current = io(SOCKET_SERVER_URL);

    // Unirse a la sala
    socketRef.current.emit('joinRoom', { room, user });

    // Escuchar mensajes
    socketRef.current.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    // Escuchar lista de usuarios
    socketRef.current.on('roomUsers', (usersList) => {
      setUsers(usersList.map(u => u.user));
    });

    // Cleanup
    return () => {
      socketRef.current.disconnect();
    };
  }, [room, user]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    // Emitir mensaje al servidor
    socketRef.current.emit('chatMessage', {
      room,
      message,
      user
    });

    setMessage('');
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Sala: {room}</h2>
      <h4>Usuarios conectados:</h4>
      <ul>
        {users.map((u, i) => (
          <li key={i}>{u.fullName}</li>
        ))}
      </ul>

      <div
        style={{
          border: '1px solid #ccc',
          padding: 10,
          height: 300,
          overflowY: 'scroll',
          marginBottom: 10,
        }}
      >
        {messages.map((msg, i) => (
          <div key={i}>
            <b>{msg.user}:</b> {msg.text} <small>({new Date(msg.timestamp).toLocaleTimeString()})</small>
          </div>
        ))}
      </div>

      <form onSubmit={sendMessage}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Escribí tu mensaje..."
          style={{ width: '80%', padding: 8, marginRight: 8 }}
        />
        <button type="submit" style={{ padding: '8px 16px' }}>
          Enviar
        </button>
      </form>
    </div>
  );
};

export default ChatRoom;
