import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';
import { useNavigate } from 'react-router-dom';

const SOCKET_SERVER_URL = 'http://localhost:5001';

const ChatRoom = ({ room }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [users, setUsers] = useState([]);
  const socketRef = useRef();
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const fullName = storedUser?.fullName || 'An√≥nimo';

    socketRef.current = io(SOCKET_SERVER_URL, {
      auth: { token },
    });

    socketRef.current.emit('joinRoom', { room, fullName });

    socketRef.current.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    socketRef.current.on('roomUsers', (usersList) => {
      setUsers(usersList);
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, [room]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    socketRef.current.emit('chatMessage', {
      room,
      text: message,
    });

    setMessage('');
  };

  const handleBack = () => {
    navigate(-1);
  };

  return (
    <div style={styles.container}>
      {/* ‚úÖ Bot√≥n volver */}
      <button onClick={handleBack} style={styles.backButton}>
        ‚Üê Volver
      </button>

      <h2 style={styles.title}>Sala: {room.charAt(0).toUpperCase() + room.slice(1)}</h2>

      <h4>Usuarios conectados:</h4>
      {users.length === 0 ? (
        <p>No hay usuarios conectados</p>
      ) : (
        <ul>
          {users.map((u) => (
            <li key={u.id}>{u.fullName}</li>
          ))}
        </ul>
      )}

      <div style={styles.chatBox}>
        {messages.map((msg, i) => (
          <div key={i}>
            <b>{msg.user || 'Anon'}:</b> {msg.text || '(sin texto)'}{' '}
            <small>({new Date(msg.timestamp).toLocaleTimeString()})</small>
          </div>
        ))}
      </div>

      <form onSubmit={sendMessage} style={styles.form}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Escrib√≠ tu mensaje..."
          style={styles.input}
          autoComplete="off"
        />
        <button type="submit" style={styles.sendButton}>
          Enviar
        </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    padding: 20,
    backgroundColor: '#4B0082',
    color: '#fff',
    minHeight: '100vh',
    position: 'relative', // üîë importante para posicionar el bot√≥n
  },
  title: {
    color: '#DAA520',
    fontSize: '2rem',
    marginBottom: 10,
    textAlign: 'center',
  },
  chatBox: {
    border: '1px solid #ccc',
    padding: 10,
    height: 300,
    overflowY: 'scroll',
    marginBottom: 10,
    backgroundColor: '#fff',
    color: '#000',
  },
  form: {
    display: 'flex',
    alignItems: 'center',
    gap: 8,
  },
  input: {
    flex: 1,
    padding: 8,
    fontSize: '1rem',
  },
  sendButton: {
    padding: '8px 16px',
    backgroundColor: '#DAA520',
    color: '#4B0082',
    border: 'none',
    borderRadius: 8,
    fontWeight: 'bold',
    cursor: 'pointer',
  },
  backButton: {
  position: 'absolute',
  top: 20, // ‚Üê en vez de bottom
  left: 20,
    backgroundColor: '#DAA520',
    color: '#4B0082',
    border: 'none',
    borderRadius: 8,
    padding: '10px 15px',
    fontWeight: 'bold',
    cursor: 'pointer',
    zIndex: 10,
  },
};

export default ChatRoom;
