import React, { useEffect, useState, useRef, useMemo } from 'react';
import { io } from 'socket.io-client';
import { useNavigate } from 'react-router-dom';
import EmojiPicker from 'emoji-picker-react';

const SOCKET_SERVER_URL = 'http://192.168.0.8:5001';  // pon tu IP local
const BASE_URL = 'http://192.168.0.8:5001/uploads/';

const signosEmoji = {
  aries: '♈', tauro: '♉', geminis: '♊', cancer: '♋', leo: '♌',
  virgo: '♍', libra: '♎', escorpio: '♏', sagitario: '♐',
  capricornio: '♑', acuario: '♒', piscis: '♓'
};

const ChatRoom = ({ room }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [usersRaw, setUsersRaw] = useState([]);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const emojiPickerRef = useRef(null);
  const [likedMessages, setLikedMessages] = useState({});
  const chatEndRef = useRef(null);
  const inputRef = useRef(null);
  const socketRef = useRef();
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const fullName = storedUser?.fullName || 'Anónimo';
    const zodiacSign = storedUser?.zodiacSign || '';

    socketRef.current = io(SOCKET_SERVER_URL, {
      auth: { token },
    });

    socketRef.current.emit('joinRoom', { room, fullName, zodiacSign, photoPath: storedUser?.photoPath || '' });

    socketRef.current.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
      setTimeout(() => {
        chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
      }, 50);
    });

    socketRef.current.on('roomUsers', (usersList) => {
      setUsersRaw(usersList);
    });

    const handleClickOutside = (e) => {
      if (emojiPickerRef.current && !emojiPickerRef.current.contains(e.target)) {
        setShowEmojiPicker(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      socketRef.current.disconnect();
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [room]);

  const users = useMemo(() => {
    return usersRaw.map(u => {
      const cleanSign = (u.zodiacSign || '')
        .toLowerCase()
        .normalize('NFD')
        .replace(/[\u0300-\u036f]/g, '');

      let photoPath = u.photoPath || '';

      if (photoPath && !photoPath.startsWith('http')) {
        const cleanedPath = photoPath.replace(/^\/?uploads\//, '');
        photoPath = `${BASE_URL}${cleanedPath}`;
      }

      return {
        ...u,
        emoji: signosEmoji[cleanSign] || '',
        photoPath: photoPath || '/default-profile.png',
      };
    });
  }, [usersRaw]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    socketRef.current.emit('chatMessage', {
      room,
      text: message.trim().replace(/[\n\r\\]+/g, ''),
    });

    setMessage('');
  };

  const handleBack = () => {
    navigate(-1);
  };

  const handleImgError = (e) => {
    e.currentTarget.onerror = null;
    if (!e.currentTarget.dataset.fallback) {
      e.currentTarget.dataset.fallback = 'true';
      e.currentTarget.src = '/default-profile.png';
    }
  };

  const toggleLike = (index) => {
    setLikedMessages((prev) => ({
      ...prev,
      [index]: !prev[index],
    }));
  };

  const addEmoji = (emojiData) => {
    setMessage((prev) => prev + emojiData.emoji);
    setShowEmojiPicker(false);
    inputRef.current?.focus();
  };

  const storedUser = JSON.parse(localStorage.getItem('user'));
  const currentUser = storedUser?.fullName || 'Anónimo';

  return (
    <div style={styles.container}>
      <button onClick={handleBack} style={styles.backButton}>
        ← Volver
      </button>

      <h2 style={styles.title}>
        Bienvenido a la sala: {room.charAt(0).toUpperCase() + room.slice(1)}
      </h2>

      <div style={styles.usersBox}>
        <h4 style={styles.usersTitle}>Usuarios conectados:</h4>
        {users.length === 0 ? (
          <p style={styles.usersText}>No hay usuarios</p>
        ) : (
          <ul style={styles.usersList}>
            {users.map((u) => (
              <li key={u._id || u.id || u.fullName} style={styles.userItem}>
                <img
                  src={u.photoPath}
                  alt={`${u.fullName} foto`}
                  style={styles.userPhoto}
                  onError={handleImgError}
                />
                <span style={{ marginRight: 8 }}>{u.emoji}</span>
                {u.fullName}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div style={styles.chatBox}>
        {messages.map((msg, i) => {
          const user = users.find(u => u.fullName === msg.user);
          const emoji = user ? user.emoji : '';
          const photo = user ? user.photoPath : '/default-profile.png';

          return (
            <div
              key={i}
              style={{
                ...styles.messageItem,
                alignSelf: 'flex-start',
                background: styles.messageItem.background,
                boxShadow: styles.messageItem.boxShadow,
                borderTopRightRadius: 10,
                borderTopLeftRadius: 10,
                opacity: 0,
                animation: 'fadeSlideIn 0.3s forwards',
                animationDelay: `${i * 100}ms`,
              }}
            >
              <img
                src={photo}
                alt={`${msg.user} foto`}
                style={styles.messagePhoto}
                onError={handleImgError}
              />
              <div style={{ maxWidth: '70vw', wordBreak: 'break-word' }}>
                <div style={styles.messageUserName}>
                  {msg.user || 'Anon'} {emoji && <span style={{ marginLeft: 5 }}>{emoji}</span>}
                </div>
                <div style={styles.messageText}>{msg.text || '(sin texto)'}</div>
                <div style={styles.messageTime}>{new Date(msg.timestamp).toLocaleTimeString()}</div>
              </div>
              <div style={styles.likeContainer}>
                <button
                  onClick={() => toggleLike(i)}
                  style={{
                    fontSize: '1.4rem',
                    background: 'transparent',
                    border: 'none',
                    cursor: 'pointer',
                    padding: 0,
                    lineHeight: 1,
                    userSelect: 'none',
                    animation: likedMessages[i] ? 'pop 0.3s ease' : 'none',
                  }}
                  aria-label={likedMessages[i] ? 'Quitar like' : 'Dar like'}
                >
                  {likedMessages[i] ? '❤️' : '🤍'}
                </button>
                <span style={styles.likeCount}>{likedMessages[i] ? 1 : 0}</span>
              </div>
            </div>
          );
        })}
        <div ref={chatEndRef} />
      </div>

      <div style={{ position: 'relative', marginBottom: 10 }}>
        <button
          type="button"
          onClick={() => setShowEmojiPicker((prev) => !prev)}
          style={{
            fontSize: '1.5rem',
            background: 'transparent',
            border: 'none',
            cursor: 'pointer',
            marginBottom: 5,
          }}
          aria-label="Mostrar/ocultar selector de emojis"
        >
          😀
        </button>
        {showEmojiPicker && (
          <div
            ref={emojiPickerRef}
            style={{
              position: 'absolute',
              zIndex: 100,
              backgroundColor: 'rgba(255, 255, 255, 0.9)',
              borderRadius: '12px',
              backdropFilter: 'blur(6px)',
              padding: '8px',
              boxShadow: '0 6px 16px rgba(0, 0, 0, 0.3)',
            }}
          >
            <EmojiPicker 
              onEmojiClick={addEmoji}
              locale="es"
              lazyLoadEmojis={true}
              localization={{
                search: "Buscar",
                clear: "Limpiar",
                notFound: "No se encontraron emojis",
                categories: {
                  recent: "Recientes",
                  smileys_people: "Emoticonos y personas",
                  animals_nature: "Animales y naturaleza",
                  food_drink: "Comida y bebida",
                  travel_places: "Viajes y lugares",
                  activities: "Actividades",
                  objects: "Objetos",
                  symbols: "Símbolos",
                  flags: "Banderas",
                  skin_tones: "Tonos de piel"
                }
              }}
            />
          </div>
        )}
      </div>

      <form onSubmit={sendMessage} style={styles.form}>
        <input
          ref={inputRef}
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Escribí tu mensaje..."
          style={styles.input}
          autoComplete="off"
        />
        <button type="submit" style={styles.sendButton}>
          Enviar
        </button>
      </form>

      <style>{`
        @keyframes fadeSlideIn {
          0% {
            opacity: 0;
            transform: translateX(-20px);
          }
          100% {
            opacity: 1;
            transform: translateX(0);
          }
        }
        @keyframes pop {
          0% { transform: scale(1); }
          50% { transform: scale(1.4); }
          100% { transform: scale(1); }
        }
      `}</style>
    </div>
  );
};

