import React, { useState, useEffect } from 'react';
import ChatRoom from '../components/ChatRoom';

const ZodiacRouletteRoom = () => {
  const [matchedRoom, setMatchedRoom] = useState('ruleta');

  // Simulación simple de emparejamiento (esto se puede cambiar luego con lógica real)
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser && storedUser.fullName) {
      const roomName = `ruleta-${storedUser.fullName.replace(/\s+/g, '').toLowerCase()}`;
      setMatchedRoom(roomName);
    }
  }, []);

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>🎲 Ruleta Zodiacal</h1>
      <p style={styles.subtitle}>
        Esperando encontrar un compañero compatible...
      </p>

      <div style={styles.chatContainer}>
        <ChatRoom room={matchedRoom} />
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: 20,
    backgroundImage: 'url("/logo/Zodiacus.jpeg")',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh',
    fontFamily: "'Poppins', sans-serif",
    color: '#fff',
  },
  title: {
    fontSize: '2.2rem',
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 10,
    color: '#DAA520',
    textShadow: '2px 2px 6px rgba(0, 0, 0, 0.85)',
  },
  subtitle: {
    fontSize: '1.1rem',
    textAlign: 'center',
    color: '#FFFFFF',
    marginBottom: 20,
    fontStyle: 'italic',
  },
  chatContainer: {
    maxWidth: '100%',
  }
};

export default ZodiacRouletteRoom;
