import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const HomeScreen = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const handleGoToRooms = () => {
    navigate('/rooms');
  };

  const handleGoToProfile = () => {
    navigate('/profile');
  };

  if (!user) {
    return <p>Cargando datos del usuario...</p>;
  }

  return (
    <div style={containerStyle}>
      <h1 style={{ color: '#DAA520' }}>¡Bienvenido, {user.fullName}!</h1>

      {/* Aquí agrego tu slogan */}
      <p style={sloganStyle}>
        La red social con mayor win rate % de parejas consolidadas y compatibilidad
      </p>

      <p style={{ fontSize: '1.2rem' }}>
        Tu signo zodiacal es <strong>{user.zodiacSign}</strong>
      </p>

      <div style={{ marginTop: '2rem', display: 'flex', flexDirection: 'column', gap: '1rem', alignItems: 'center' }}>
        <button onClick={handleGoToProfile} style={buttonStyle}>
          Mi Perfil
        </button>

        <button onClick={handleGoToRooms} style={buttonStyle}>
          Ir a salas por signo
        </button>

        <button
          onClick={handleLogout}
          style={{ ...buttonStyle, backgroundColor: '#ccc', color: '#4B0082' }}
        >
          Cerrar sesión
        </button>
      </div>
    </div>
  );
};

const containerStyle = {
  height: '100vh',
  backgroundColor: '#4B0082',
  color: '#fff',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
  textAlign: 'center',
  padding: '2rem',
};

const sloganStyle = {
  fontSize: '1.3rem',
  color: '#fff',
  fontWeight: '600',
  margin: '0.5rem 0 1.5rem 0',
};

const buttonStyle = {
  width: 200,
  padding: '1rem',
  backgroundColor: '#DAA520',
  color: '#4B0082',
  border: 'none',
  borderRadius: '1rem',
  fontSize: '1rem',
  cursor: 'pointer',
  fontWeight: 'bold',
};

export default HomeScreen;
