import React, { useEffect, useState, useRef, useMemo } from 'react';
import { io } from 'socket.io-client';
import { useNavigate } from 'react-router-dom';
import EmojiPicker from 'emoji-picker-react';

const SOCKET_SERVER_URL = 'http://localhost:5001';
const BASE_URL = 'http://localhost:5001/uploads/';

const signosEmoji = {
  aries: '‚ôà', tauro: '‚ôâ', geminis: '‚ôä', cancer: '‚ôã', leo: '‚ôå',
  virgo: '‚ôç', libra: '‚ôé', escorpio: '‚ôè', sagitario: '‚ôê',
  capricornio: '‚ôë', acuario: '‚ôí', piscis: '‚ôì'
};

const ZodiacRouletteRoom = () => {
  const [partner, setPartner] = useState(null);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [waiting, setWaiting] = useState(true);
  const [room, setRoom] = useState(null);
  const [usersRaw, setUsersRaw] = useState([]);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const emojiPickerRef = useRef(null);
  const chatEndRef = useRef(null);
  const inputRef = useRef(null);
  const socketRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    const token = localStorage.getItem('token');
    if (!storedUser || !token) return;

    socketRef.current = io(SOCKET_SERVER_URL, { auth: { token } });

    // Pedir un partner en la ruleta
    socketRef.current.emit('joinRoulette', storedUser);

    // Cuando nos asignan un partner y una sala
    socketRef.current.on('roulettePartner', (data) => {
      console.log('üé≤ Asignado a sala de ruleta:', data.room);
      setPartner(data);
      setRoom(data.room);
      setWaiting(false);
      setMessages([]);
      setUsersRaw([]);

      // Unirse a la sala para mensajes y usuarios
      socketRef.current.emit('joinRoom', {
        room: data.room,
        fullName: storedUser.fullName,
        zodiacSign: storedUser.zodiacSign,
        photoPath: storedUser.photoPath,
      });
      // Solicitar la lista de usuarios actualizada tras unirse a la sala
      socketRef.current.emit('getRoomUsers', data.room);
    });

    socketRef.current.on('waitingPartner', () => {
      setWaiting(true);
      setPartner(null);
      setMessages([]);
      setUsersRaw([]);
    });

    // Escuchar mensajes en la sala
    socketRef.current.on('message', (msg) => {
      setMessages((prev) => [...prev, msg]);
      setTimeout(() => chatEndRef.current?.scrollIntoView({ behavior: 'smooth' }), 50);
    });

    // Escuchar usuarios conectados en la sala
    socketRef.current.on('roomUsers', (usersList) => {
      console.log('üë• Usuarios en sala:', usersList);
      setUsersRaw(usersList);
    });

    return () => {
      socketRef.current.disconnect();
      setRoom(null);
      setUsersRaw([]);
      setMessages([]);
      setPartner(null);
    };
  }, []);

  // Procesar usuarios para mostrar emoji y foto limpia
  const users = useMemo(() => {
    return usersRaw.map(u => {
      const cleanSign = (u.zodiacSign || '')
        .toLowerCase()
        .normalize('NFD')
        .replace(/[\u0300-\u036f]/g, '');

      let photoPath = u.photoPath || '';
      if (photoPath && !photoPath.startsWith('http')) {
        const cleanedPath = photoPath.replace(/^\/?uploads\//, '');
        photoPath = `${BASE_URL}${cleanedPath}`;
      }

      return {
        ...u,
        emoji: signosEmoji[cleanSign] || '',
        photoPath: photoPath || '/default-profile.png',
      };
    });
  }, [usersRaw]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (!message.trim() || !room) return;

    socketRef.current.emit('chatMessage', {
      room,
      text: message.trim(),
    });

    setMessage('');
  };

  const handleBack = () => {
    navigate(-1);
  };

  const handleImgError = (e) => {
    e.currentTarget.onerror = null;
    e.currentTarget.src = '/default-profile.png';
  };

  const addEmoji = (emojiData) => {
    setMessage((prev) => prev + emojiData.emoji);
    setShowEmojiPicker(false);
    inputRef.current?.focus();
  };

  // Partner con foto y emoji procesados
  const partnerWithExtras = useMemo(() => {
    if (!partner) return null;
    const cleanSign = (partner.zodiacSign || '')
      .toLowerCase()
      .normalize('NFD')
      .replace(/[\u0300-\u036f]/g, '');

    let photoPath = partner.photoPath || '';
    if (photoPath && !photoPath.startsWith('http')) {
      const cleanedPath = photoPath.replace(/^\/?uploads\//, '');
      photoPath = `${BASE_URL}${cleanedPath}`;
    }

    return {
      ...partner,
      emoji: signosEmoji[cleanSign] || '',
      photoPath: photoPath || '/default-profile.png',
    };
  }, [partner]);

  return (
    <div style={styles.container}>
      <button onClick={handleBack} style={styles.backButton}>
        ‚Üê Volver
      </button>

      <h2 style={styles.title}>üé≤ Ruleta Zodiacal üîÆ</h2>

      {waiting ? (
        <div style={styles.infoBox}>
          <h3 style={styles.infoTitle}>Buscando usuario...</h3>
          <p style={styles.infoText}>
            En esta sala de ruleta zodiacal, ser√°s emparejado aleatoriamente con otro usuario.
            El objetivo es crear nuevas conexiones, ya sea una charla casual, una amistad o lo que surja. ¬°Disfrut√° la experiencia c√≥smica!
          </p>
        </div>
      ) : (
        <>
          {partnerWithExtras && (
            <div style={styles.usersBox}>
              <h4 style={styles.usersTitle}>Tu pareja en la ruleta:</h4>
              <div style={styles.userItem}>
                <img
                  src={partnerWithExtras.photoPath}
                  alt={`${partnerWithExtras.fullName} foto`}
                  style={styles.userPhoto}
                  onError={handleImgError}
                />
                <span style={{ marginRight: 8 }}>{partnerWithExtras.emoji}</span>
                {partnerWithExtras.fullName}
              </div>
            </div>
          )}

          <div style={styles.usersBox}>
            <h4 style={styles.usersTitle}>Usuarios conectados en sala:</h4>
            {users.length === 0 ? (
              <p style={styles.usersText}>No hay usuarios conectados</p>
            ) : (
              <ul style={styles.usersList}>
                {users.map((u) => (
                  <li key={`${u.id || u._id || u.fullName}-${u.zodiacSign}`} style={styles.userItem}>
                    <img
                      src={u.photoPath}
                      alt={`${u.fullName} foto`}
                      style={styles.userPhoto}
                      onError={handleImgError}
                    />
                    <span style={{ marginRight: 8 }}>{u.emoji}</span>
                    {u.fullName}
                  </li>
                ))}
              </ul>
            )}
          </div>

          <div style={styles.chatBox}>
            {messages.map((msg, i) => (
              <div key={msg.id || i} style={styles.messageItem}>
                <div style={styles.messageUserName}>{msg.user || 'Usuario'}:</div>
                <div style={styles.messageText}>{msg.text}</div>
              </div>
            ))}
            <div ref={chatEndRef} />
          </div>

          <div style={{ position: 'relative', marginBottom: 10 }}>
            <button
              type="button"
              onClick={() => setShowEmojiPicker((prev) => !prev)}
              style={{
                fontSize: '1.5rem',
                background: 'transparent',
                border: 'none',
                cursor: 'pointer',
                marginBottom: 5,
                color: '#4B0082',
              }}
              aria-label="Mostrar/ocultar selector de emojis"
            >
              üòÄ
            </button>
            {showEmojiPicker && (
              <div
                ref={emojiPickerRef}
                style={{
                  position: 'absolute',
                  zIndex: 100,
                  backgroundColor: 'rgba(255, 255, 255, 0.9)',
                  borderRadius: '12px',
                  backdropFilter: 'blur(6px)',
                  padding: '8px',
                  boxShadow: '0 6px 16px rgba(0, 0, 0, 0.3)',
                }}
              >
                <EmojiPicker
                  onEmojiClick={addEmoji}
                  locale="es"
                  lazyLoadEmojis={true}
                  localization={{
                    search: "Buscar",
                    clear: "Limpiar",
                    notFound: "No se encontraron emojis",
                    categories: {
                      recent: "Recientes",
                      smileys_people: "Emoticonos y personas",
                      animals_nature: "Animales y naturaleza",
                      food_drink: "Comida y bebida",
                      travel_places: "Viajes y lugares",
                      activities: "Actividades",
                      objects: "Objetos",
                      symbols: "S√≠mbolos",
                      flags: "Banderas",
                      skin_tones: "Tonos de piel"
                    }
                  }}
                />
              </div>
            )}
          </div>

          <form onSubmit={sendMessage} style={styles.form}>
            <input
              ref={inputRef}
              type="text"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Escrib√≠ tu mensaje..."
              style={styles.input}
              autoComplete="off"
            />
            <button type="submit" style={styles.sendButton}>
              Enviar
            </button>
          </form>
        </>
      )}
    </div>
  );
};

