import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const RegisterScreen = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullName: '',
    birthDate: '',
    dni: null,
    password: ''
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'dni') {
      setFormData({ ...formData, dni: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ac치 ir칤a la l칩gica para enviar los datos al backend
    console.log('游늶 Registrando usuario:', formData);
    navigate('/login');
  };

  return (
    <div style={{
      height: '100vh',
      backgroundColor: '#4B0082',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#fff',
      padding: '2rem'
    }}>
      <form onSubmit={handleSubmit} style={{
        backgroundColor: '#fff',
        padding: '2rem',
        borderRadius: '1rem',
        boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
        width: '100%',
        maxWidth: '400px',
        color: '#4B0082'
      }}>
        <h2 style={{ textAlign: 'center', marginBottom: '1.5rem', color: '#4B0082' }}>
          Registro de Usuario
        </h2>

        <div style={{ marginBottom: '1rem' }}>
          <label>Nombre completo:</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        <div style={{ marginBottom: '1rem' }}>
          <label>Fecha de nacimiento:</label>
          <input
            type="date"
            name="birthDate"
            value={formData.birthDate}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        <div style={{ marginBottom: '1rem' }}>
          <label>DNI (foto o escaneo):</label>
          <input
            type="file"
            name="dni"
            accept="image/*,.pdf"
            onChange={handleChange}
            required
            style={{ marginTop: '0.3rem' }}
          />
        </div>

        <div style={{ marginBottom: '1.5rem' }}>
          <label>Contrase침a:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
            style={inputStyle}
          />
        </div>

        <button type="submit" style={buttonStyle}>
          Registrarse
        </button>
      </form>
    </div>
  );
};

const inputStyle = {
  width: '100%',
  padding: '0.8rem',
  border: '1px solid #ccc',
  borderRadius: '8px',
  fontSize: '1rem',
  marginTop: '0.3rem'
};

const buttonStyle = {
  width: '100%',
  padding: '0.8rem',
  backgroundColor: '#DAA520',
  color: '#4B0082',
  border: 'none',
  borderRadius: '8px',
  fontSize: '1rem',
  fontWeight: 'bold',
  cursor: 'pointer'
};

export default RegisterScreen;
