import { useState } from 'react';
import Layout from '../components/Layout';
import Button from '../components/Button';
import colors from '../styles/colors';

const RegisterScreen = () => {
  const [fullName, setFullName] = useState('');
  const [dob, setDob] = useState('');
  const [dniFile, setDniFile] = useState(null);
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!fullName || !dob || !dniFile || !password) {
      alert('Por favor completá todos los campos incluyendo la contraseña.');
      return;
    }

    const formData = new FormData();
    formData.append('fullName', fullName);
    formData.append('dob', dob);
    formData.append('dni', dniFile);
    formData.append('password', password);

    try {
      const res = await fetch('http://localhost:5001/api/register', {
        method: 'POST',
        body: formData,
      });

      const data = await res.json();

      if (!res.ok) {
        throw new Error(data.error || 'Error en el registro');
      }

      alert('¡Registro exitoso!');
      // Podés redirigir o limpiar formulario aquí
    } catch (error) {
      alert(`Error: ${error.message}`);
    }
  };

  return (
    <Layout>
      <h2 style={{ color: colors.primary }}>Registro de Usuario</h2>
      <form onSubmit={handleSubmit} style={{ maxWidth: 400 }}>
        {/* Nombre completo */}
        <div style={{ marginBottom: 10 }}>
          <label>Nombre completo:</label><br />
          <input
            type="text"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '8px',
              backgroundColor: 'white',
              border: `1px solid ${colors.primary}`,
              borderRadius: '4px',
            }}
          />
        </div>

        {/* Fecha de nacimiento */}
        <div style={{ marginBottom: 10 }}>
          <label>Fecha de nacimiento:</label><br />
          <input
            type="date"
            value={dob}
            onChange={(e) => setDob(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '8px',
              backgroundColor: 'white',
              border: `1px solid ${colors.primary}`,
              borderRadius: '4px',
            }}
          />
        </div>

        {/* DNI */}
        <div style={{ marginBottom: 10 }}>
          <label>Subí tu DNI:</label><br />
          <input
            type="file"
            accept="image/*,.pdf"
            onChange={(e) => setDniFile(e.target.files[0])}
            required
          />
        </div>

        {/* Contraseña */}
        <div style={{ marginBottom: 10 }}>
          <label>Contraseña:</label><br />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{
              width: '100%',
              padding: '8px',
              backgroundColor: 'white',
              border: `1px solid ${colors.primary}`,
              borderRadius: '4px',
            }}
          />
        </div>

        <Button>Registrarme</Button>
      </form>
    </Layout>
  );
};

export default RegisterScreen;
