import React from 'react';
import { useNavigate } from 'react-router-dom';

const signos = [
  { name: 'Aries', emoji: '♈' },
  { name: 'Tauro', emoji: '♉' },
  { name: 'Géminis', emoji: '♊' },
  { name: 'Cáncer', emoji: '♋' },
  { name: 'Leo', emoji: '♌' },
  { name: 'Virgo', emoji: '♍' },
  { name: 'Libra', emoji: '♎' },
  { name: 'Escorpio', emoji: '♏' },
  { name: 'Sagitario', emoji: '♐' },
  { name: 'Capricornio', emoji: '♑' },
  { name: 'Acuario', emoji: '♒' },
  { name: 'Piscis', emoji: '♓' },
];

const ZodiacRoomsScreen = () => {
  const navigate = useNavigate();

  const handleEnterRoom = (sign) => {
    navigate(`/rooms/${sign.toLowerCase()}`);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Salas Zodiacales</h1>
      <div style={styles.grid}>
        {signos.map(({ name, emoji }) => (
          <div key={name} style={styles.card}>
            <div style={styles.emoji}>{emoji}</div>
            <h2 style={styles.signName}>{name}</h2>
            <button
              style={styles.button}
              onClick={() => handleEnterRoom(name)}
            >
              Entrar
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: 20,
    backgroundColor: '#4B0082',
    minHeight: '100vh',
    color: 'white',
  },
  title: {
    textAlign: 'center',
    color: '#DAA520',
    marginBottom: 30,
    fontSize: '2.5rem',
  },
  grid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fit, minmax(140px, 1fr))',
    gap: 20,
    maxWidth: 900,
    margin: '0 auto',
  },
  card: {
    backgroundColor: '#311B58',
    borderRadius: 12,
    padding: 20,
    textAlign: 'center',
    boxShadow: '0 4px 10px rgba(218, 165, 32, 0.5)',
    transition: 'transform 0.2s ease',
    cursor: 'pointer',
  },
  emoji: {
    fontSize: '4rem',
    marginBottom: 10,
  },
  signName: {
    marginBottom: 15,
    fontWeight: 'bold',
    fontSize: '1.3rem',
    color: '#DAA520',
  },
  button: {
    backgroundColor: '#DAA520',
    border: 'none',
    borderRadius: 8,
    color: '#4B0082',
    fontWeight: 'bold',
    padding: '10px 15px',
    fontSize: '1rem',
    cursor: 'pointer',
  },
};

export default ZodiacRoomsScreen;
