import React, { useState } from 'react';
import { View, Text, TextInput, Button, Alert, StyleSheet } from 'react-native';
import axios from 'axios';

const LoginScreen = ({ navigation }) => {
  const [dni, setDni] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    if (!dni || !password) {
      Alert.alert('Error', 'Por favor ingresa DNI y contraseña.');
      return;
    }

    try {
      const formData = new FormData();
      formData.append('dni', dni);
      formData.append('password', password);

      const response = await axios.post('http://localhost:5001/api/auth/login', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const { token, userId, message } = response.data;

      Alert.alert('Éxito', message);
      navigation.navigate('Salas', { token, userId });
    } catch (error) {
      console.error(error);
      Alert.alert('Error', 'Credenciales inválidas o error del servidor.');
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Iniciar Sesión</Text>

      <TextInput
        style={styles.input}
        placeholder="DNI"
        value={dni}
        onChangeText={setDni}
        keyboardType="default"
      />

      <TextInput
        style={styles.input}
        placeholder="Contraseña"
        value={password}
        onChangeText={setPassword}
        secureTextEntry
      />

      <Button title="Ingresar" onPress={handleLogin} color="#4B0082" />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#DAA520',
    justifyContent: 'center',
    padding: 20,
  },
  title: {
    fontSize: 26,
    marginBottom: 30,
    textAlign: 'center',
    color: '#fff',
    fontWeight: 'bold',
  },
  input: {
    backgroundColor: '#fff',
    borderRadius: 10,
    paddingHorizontal: 15,
    paddingVertical: 10,
    fontSize: 16,
    marginBottom: 15,
  },
});

export default LoginScreen;
