import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

const LoginScreen = () => {
  const { login } = useContext(AuthContext); // ✅ usamos login desde el contexto
  const navigate = useNavigate();

  const [fullName, setFullName] = useState('');
  const [dob, setDob] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Intentando iniciar sesión con:', { fullName, dob, password });

    if (!fullName || !dob || !password) {
      setError('Por favor completá todos los campos.');
      return;
    }

    try {
      const res = await fetch('http://localhost:5001/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ fullName, dob, password }),
      });

      console.log('Respuesta del backend status:', res.status);
      const data = await res.json();
      console.log('Respuesta del backend:', data);

      if (!res.ok) {
        setError(data.message || 'Error al iniciar sesión.');
        return;
      }

      // Guardamos el token y el usuario
      localStorage.setItem('token', data.token);
      localStorage.setItem('user', JSON.stringify(data.user));

      // Actualizamos contexto global
      login(data.user);

      console.log('Login exitoso, navegando a /home');
      navigate('/home');
    } catch (err) {
      console.error('Error en login:', err);
      setError('Error al conectar con el servidor.');
    }
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>Iniciar sesión</h2>
      <form onSubmit={handleSubmit} style={formStyle}>
        <input
          type="text"
          placeholder="Nombre completo"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          style={inputStyle}
        />
        <input
          type="date"
          value={dob}
          onChange={(e) => setDob(e.target.value)}
          style={inputStyle}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={inputStyle}
        />
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit" style={buttonStyle}>
          Ingresar
        </button>
      </form>
    </div>
  );
};

const containerStyle = {
  height: '100vh',
  backgroundColor: '#4B0082',
  color: '#fff',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
};

const titleStyle = {
  color: '#DAA520',
  marginBottom: '1rem',
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '1rem',
  width: '300px',
};

const inputStyle = {
  padding: '0.8rem',
  borderRadius: '0.5rem',
  border: 'none',
  fontSize: '1rem',
};

const buttonStyle = {
  padding: '0.8rem',
  backgroundColor: '#DAA520',
  color: '#4B0082',
  border: 'none',
  borderRadius: '0.8rem',
  fontSize: '1rem',
  cursor: 'pointer',
};

export default LoginScreen;
